import json
import os
from pathlib import Path


class Config:
    _instance = None

    def __new__(cls):
        if cls._instance is None:
            cls._instance = super().__new__(cls)
            # Initialize attributes with default values or None
            cls._instance.logo_path = None
            cls._instance.apps = None
            cls._instance.upload_config = None
            cls._instance.upload_url = None
        return cls._instance

    def load_config(self, config_file: Path) -> None:
        """Load configuration from a JSON file and update the singleton attributes."""
        self.logo_path = f"{Path(__file__).parent.parent / Path('assets/img/logo.png')}"

        app = os.getenv("APP_NAME", None)
        server = os.getenv("SERVER", "http://gpu_server:1873")

        with config_file.open('r', encoding='utf-8') as f:
            data = json.load(f)

        self.apps = dict()
        if app:
            self.apps[app] = dict(url=server, config=data.copy())
            self.apps[app]["config"]["app"]["repository"] = f"/rag/{app}"
            self.apps[app]["config"]["parameters"]["input"]["rag"]["reindex"] = False

        self.upload_config = data.copy()
        self.upload_url = server


